import firebase from "firebase/app";
import "firebase/auth";

const collection = (collectionName) => firebase.firestore().collection(collectionName);

export const logoutUser = () => {
  firebase.auth().signOut();
};

export const signInUser = async ({ name, email, password }) => {
  try {
    cred = await firebase.auth().createUserWithEmailAndPassword(email, password);
    users_db = await collection('users')

    users_db.doc(cred.user.uid).set({
      id: firebase.auth().currentUser.uid,
      name: name,
      bio: "",
      ghin: "",
      username: "",
      isPrivate: false
      // profile_photo : "data:image/png;base64,"
    })
    // users_db.doc(cred.user.uid).collection('followers').add({
    //   user_id: "prCblMeJN2M4hcd43RVA9sM6Ow03"
    // })
    // users_db.doc(cred.user.uid).collection('following').add({
    //   user_id: "prCblMeJN2M4hcd43RVA9sM6Ow03"
    // })    
    // users_db.doc(cred.user.uid).collection('posts').add({
    //   post_id: "0h9EoL27ufHBN1R8C9P6"
    // })    
    firebase.auth().currentUser.updateProfile({
      displayName: name
    });

    return {};
  } catch (error) {
    switch (error.code) {
      case "auth/email-already-in-use":
        return {
          error: "E-mail already in use."
        };
      case "auth/invalid-email":
        return {
          error: "Invalid e-mail address format."
        };
      case "auth/weak-password":
        return {
          error: "Password is too weak."
        };
      case "auth/too-many-requests":
        return {
          error: "Too many request. Try again in a minute."
        };
      default:
        return {
          error: "Check your internet connection."
        };
    }
  }
};

export const loginUser = async ({ email, password }) => {
  try {
    await firebase.auth().signInWithEmailAndPassword(email, password);
    return {};
  } catch (error) {
    switch (error.code) {
      case "auth/invalid-email":
        return {
          error: "Invalid email address format."
        };
      case "auth/user-not-found":
      case "auth/wrong-password":
        return {
          error: "Invalid email address or password."
        };
      case "auth/too-many-requests":
        return {
          error: "Too many request. Try again in a minute."
        };
      default:
        return {
          error: "Check your internet connection."
        };
    }
  }
};

export const sendEmailWithPassword = async email => {
  try {
    await firebase.auth().sendPasswordResetEmail(email);
    return {};
  } catch (error) {
    switch (error.code) {
      case "auth/invalid-email":
        return {
          error: "Invalid email address format."
        };
      case "auth/user-not-found":
        return {
          error: "User with this email does not exist."
        };
      case "auth/too-many-requests":
        return {
          error: "Too many request. Try again in a minute."
        };
      default:
        return {
          error: "Check your internet connection."
        };
    }
  }
};
