import React, {Component} from 'react'
import { View, Text, StyleSheet,FlatList,Image,TouchableHighlight,RefreshControl,TouchableOpacity } from 'react-native'
import UserPostList from '../components/Profile/UserPostList';
import Fire from "../Fire";
import firebase from "firebase";
import { set } from 'react-native-reanimated';
import {useNavigation} from '@react-navigation/native'
import {ProfileService} from '../api/get'

const profileImageSize = 36;
const padding = 12;

class ActivityScreen extends Component {

    state = {
      loading: false,
      users: []
    };

    componentDidMount() {
      this.makeRemoteRequest()
    }  
    makeRemoteRequest = async () => {
      // If we are currently getting posts, then bail out..
      if (this.state.loading) {
        return;
      }      

      this.setState({ loading: true });

      // The data prop will be an array of posts, the cursor will be used for pagination.
      const p = new ProfileService
      const users = await p.getPros()
      // const { data } = await Fire.shared.getProfile();
      // console.log(users)
      
      this.setState({ 
        users : users,
        // posts: Object.values(data).sort((a, b) => a.timestamp < b.timestamp)
      })
      // Finish loading, this will stop the refreshing animation.
      this.setState({ loading: false });
    };    
    _onRefresh = () => this.makeRemoteRequest();

    
    render() {
        // if (!this.props.user) return null;
    
        // const userPosts = this.props.feed.filter( item => item.authorId === this.props.user.id)
        // console.log(this.state.users)  
    
        return (
      <View style={styles.container}>
        <FlatList
            data={this.state.users} 
            renderItem={({item})=> <ListItem user={item} />}
            refreshControl={
                <RefreshControl
                  refreshing={this.state.loading}
                  onRefresh={this._onRefresh}
                />
              }   
        />
      </View>
      );
    }
  }


